const lightSwitch=document.getElementById("lightSwitch");window.addEventListener("load",function(){if(lightSwitch){initTheme();lightSwitch.addEventListener("change",function(){resetTheme()})}});function initTheme(){const lightThemeSelected=localStorage.getItem("lightSwitch")!==null&&localStorage.getItem("lightSwitch")==="light";lightSwitch.checked=lightThemeSelected;lightThemeSelected?document.body.setAttribute("data-theme","light"):document.body.removeAttribute("data-theme")}function resetTheme(){if(lightSwitch.checked){document.body.setAttribute("data-theme","light");localStorage.setItem("lightSwitch","light")}else{document.body.removeAttribute("data-theme");localStorage.removeItem("lightSwitch")}}